    def decode_icon(self,icon,widget):
        tmp_file = open("tmp.png",'wb')
        tmp_file.write(base64.b64decode(icon))
        tmp_file.close()
        widget.setIcon(QIcon("tmp.png"))
        os.remove("tmp.png")
                        
                        
